import React, { useEffect, useState } from "react";
import InputGroup from "../components/InputGroup";
import RowDetails from "../components/RowDetails";
import axios from "axios";

const Home = () => {
  const [users, setUsers] = useState([]);
  useEffect(async () => {
    await axios.get("/api/users").then((res) => {
      setUsers(res.data);
    });
  });

  return (
    <div className="row p-4">
      <div
        className="alert alert-success d-flex align-items-center"
        role="alert"
      >
        <svg
          className="bi flex-shrink-9 me-2"
          width="24"
          height="24"
          role="img"
          aria-label="Success"
        >
          <use xlinkHref="#check-circle-fill" />
        </svg>
        <div>An example success alert with an icon</div>
      </div>
      <div className="mt-4">
        <h2>Crud Users</h2>
      </div>

      <div className="col-12 col-lg-4">
        <form>
          <InputGroup label="Email" type="text" name="Email" />
          <InputGroup label="Lastname" type="text" name="Firstname" />
          <InputGroup label="Firstname" type="text" name="Lastname" />
          <InputGroup label="Age" type="text" name="Age" />
          <button className="btn btn-primary">Add user</button>
        </form>
      </div>
      <div className="col-12 col-lg-7">
        <table className="table">
          <thead className="table">
            <tr>
              <th scope="col">Email</th>
              <th scope="col">Lastname</th>
              <th scope="col">Firstname</th>
              <th scope="col">Age</th>
              <th scope="col">Actions</th>
            </tr>
          </thead>

          <tbody>
            {users.map(({ Email, Lastname, Firstname, Age, _id }) => (
              <RowDetails
                Email={Email}
                Lastname={Lastname}
                Firstname={Firstname}
                Age={Age}
                Id={_id}
              />
            ))}
            <RowDetails />
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Home;
