import React from "react";

const InputGroup = ({ label, type, name, onChangeHandler }) => {
  return (
    <div className="mb-3">
      <label htmlFor={name} className="form-label">
        {label}
      </label>
      <input
        type={type}
        className="form-control"
        name={name}
        onChange={onChangeHandler}
      />
    </div>
  );
};

export default InputGroup;
